pragma solidity ^0.8.0;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// 支付核心:手续费和Titan代扣
contract PayCore is Ownable {
    address public titan; // titan代币地址
    address private _feeReceiver; // titan手续费接收地址 与belly付款接收地址
    address public belly; // belly代币地址
    uint16 private _feeRatio = 300; // 手续费率（单位万分比）

    constructor(address _titan) {
        _feeReceiver = msg.sender;
        titan = _titan;
    }

    function setTitan(address _titan) external onlyOwner {
        require(_titan != address(0), "PayCore: titan can not be zero");
        titan = _titan;
    }

    function set_feeRatio(uint16 _feeRatio) external onlyOwner {
        require(_feeRatio <= 8000, "PayCore: ratio more than 8000");
        _feeRatio = _feeRatio;
    }

    function setFeeReciver(address _feeReceiver) external onlyOwner {
        require(_feeReceiver != address(0), "PayCore: reciver can not be zero");
        _feeReceiver = _feeReceiver;
    }

    function feeInfo(uint256 _price)
        external
        view
        returns (address _receiver, uint256 _fee)
    {
        return _feeInfo(_price);
    }

    //手续费查询
    function _feeInfo(uint256 _price)
        internal
        view
        returns (address, uint256)
    {
        return (_feeReceiver, (_price * _feeRatio) / 10000);
    }

    function payTitan(
        address _from,
        address _to,
        uint256 _price
    ) public returns(bool){
        uint256 value = _price;
        // 手续费收取
        (address feeAddr, uint256 fee) = _feeInfo(_price);
        if (fee > 0) {
            value -= fee;
            IERC20(titan).transferFrom(_from,feeAddr, fee);
        }
        // 卖家收取
        IERC20(titan).transferFrom(_from,_to, value);
        return true;
    }

    function payBelly(
        address _from,
        uint256 _price
    ) public returns(bool){
        IERC20(belly).transferFrom(_from,_feeReceiver, _price);
        return true;
    }
}
